mapper.select:
select 
from t_broadcast_message t_broadcast_message



            and ${params.map.timeCol} >= #{params.map.startTime}


            and ${params.map.timeCol} < #{params.map.endTime}

select 
from t_mail t_mail



            and ${params.map.timeCol} >= #{params.map.startTime}


            and ${params.map.timeCol} < #{params.map.endTime}
